mapper.select:
/**selectShotCount*/
/**해당홀 샷 여부*/
SELECT COUNT(1)
FROM kpga.track3d_shot_info t1
WHERE t1.game_id  = #{gameId}
  AND t1.round_no = #{roundNo}
 AND t1.hole_no = #{holeNo}

mapper.update:
/**updateNearpinEndDate*/
/**니어핀종료*/
UPDATE kpga.event_near t1
SET t1.end_date = NOW()
WHERE t1.event_sq = #{eventSq}
  AND t1.round_no = #{roundNo}

/**updateGameRoundState*/
/**라운드상태수정*/
UPDATE kpga_db.game_round t1
SET t1.round_state = '2'
WHERE t1.game_id   = #{gameId}
  AND t1.round_no  = #{roundNo}

/**updateKpsaToGms*/
UPDATE kpga.kpsa_vod_mst
SET mod_user = 'schedule'
WHERE vod_seq = #{vodSeq}

mapper.select:
/**selectSchedulerAll*/
SELECT * FROM game_point_dsbtbl t1 WHERE t1.game_id LIKE '2023%' SELECT * FROM game_player t1 WHERE t1.game_id LIKE '2023%' SELECT * FROM game_qlf_player t1 WHERE t1.game_id LIKE '2023%' SELECT * FROM game_round_starttime t1 WHERE t1.game_id LIKE '2022%' SELECT * FROM kpga_db.game_score WHERE game_id LIKE '2023%' SELECT * FROM game_score_org t1 WHERE t1.game_id LIKE '2023%' SELECT * FROM game_score_record t1 WHERE t1.game_id LIKE '2023%' SELECT * FROM game_year_gmcnt t1 WHERE t1.game_year = '2023' SELECT * FROM game_year_point t1 WHERE t1.game_year = '2023' SELECT * FROM game_year_prize t1 WHERE t1.game_year = '2023' SELECT * FROM incs_tx_item t1 WHERE t1.tx_moid LIKE '23%' SELECT * FROM incs_tx_mst t1 WHERE t1.tx_moid LIKE '23%' SELECT * FROM mgmt_tour_seed t1 WHERE t1.apc_year = '2023' SELECT * FROM player_nonofficial_record_year t1 WHERE t1.game_year = '2023' SELECT * FROM player_nonofficial_record_year_before t1 WHERE t1.game_year = '2023' SELECT * FROM player_record_year t1 WHERE t1.game_year = '2023' SELECT * FROM player_record_year_before t1 WHERE t1.game_year = '2023' SELECT * FROM player_user_mst_hist t1 WHERE t1.hist_dttm > '2023-01-01 00:00:00' SELECT * FROM player_year_round t1 WHERE t1.game_year = '2023' SELECT * FROM kpga_db.${tableName}
LIMIT #{pageNo}, 10000
